<span class="line"><span style="color:#002339;">        </span><span style="color:#7B30D0;">return</span><span style="color:#002339;"> </span><span style="color:#2F86D2;">Reflect</span><span style="color:#002339;">.</span><span style="color:#7EB233;">set</span><span style="color:#002339;">(</span><span style="color:#2F86D2;">target</span><span style="color:#002339;">, </span><span style="color:#2F86D2;">prop</span><span style="color:#002339;">, </span><span style="color:#2F86D2;">value</span><span style="color:#002339;">)</span></span>
